import React, { useContext,useState } from 'react'
import './CartItem.css'
import { ShopContext } from '../../Context/ShopContext'
import cross from '../Assets/cross.png'
import  AuthContext  from '../../Context/LoginAuthProvider'
const CartItems = () => {
       const {getTotalCartAmount, products,cartItems,removeFromCart} =useContext(ShopContext);
       const { auth } = useContext(AuthContext); 
       console.log("navpage auth:",auth.name,auth.id);



//handle sumbit order
const [isSubmitting, setIsSubmitting] = useState(false); // Track submission status
  const [errorMessage, setErrorMessage] = useState(""); // To store error messages
  const [showAddressForm, setShowAddressForm] = useState(false); // To show address form if needed


       const submitOrder = async () => {
        const token = localStorage.getItem("token"); // Assuming token is stored in localStorage
        console.log("here is the token",token);
        if (!token) {
          alert("You must be logged in to submit an order.");
          return;
        }

        // Fetch the user profile (address and phone number) to check if they are filled
        const userProfileResponse = await fetch('/api/user/profile', {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${token}`,
          },
        });

        const userProfile = await userProfileResponse.json();

        if (!userProfile || !userProfile.address || !userProfile.phoneNumber) {
          // If the user doesn't have an address or phone number, show the form to input them
          setShowAddressForm(true);
          return;
        }

        // Prepare the order items, shipping address, and payment method
        const orderItems = products
          .filter(product => cartItems[product.id] > 0)
          .map(product => ({
            id: product.id,
            name: product.ProductName,
            price: product.ProductPrice,
            quantity: cartItems[product.id],
          }));

        const shippingAddress = userProfile.address;
        const paymentMethod = "Credit Card"; // This can be captured dynamically if needed

        const orderData = {
          orderItems,
          shippingAddress,
          paymentMethod,
        };

        try {
          setIsSubmitting(true);
          setErrorMessage(""); // Clear previous errors

          // Send order data to the backend API
          const response = await fetch('/api/orders', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
              'Authorization': `Bearer ${token}`,
            },
            body: JSON.stringify(orderData),
          });

          const result = await response.json();

          if (response.ok) {
            // Order placed successfully
            alert("Your order has been placed successfully!");
            // Clear cart after successful order (optional)
            // clearCart();
          } else {
            // Handle error from server
            setErrorMessage(result.message || "An error occurred while placing the order.");
          }
        } catch (error) {
          console.error('Error placing order:', error);
          setErrorMessage("An error occurred while placing the order. Please try again later.");
        } finally {
          setIsSubmitting(false);
        }
      };

      // Handle address form submission
      const handleAddressSubmit = async (address, phoneNumber) => {
        const token = localStorage.getItem("token");

        // Send updated address and phone number to the server
        const response = await fetch('/api/user/profile', {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`,
          },
          body: JSON.stringify({ address, phoneNumber }),
        });

        const result = await response.json();

        if (response.ok) {
          // After updating the profile, proceed to submit the order
          setShowAddressForm(false);
          submitOrder();
        } else {
          setErrorMessage(result.message || "Error updating your profile. Please try again.");
        }
      };













  return (
    <div className='cartitems'>
      <table className='cart-table'>
    <thead>
      <tr className='cart-table-header'>
        <th>Product</th>
        <th>Title</th>
        <th>Size</th>
        <th>Price</th>
        <th>Quantity</th>
        <th>Total</th>
        <th>Remove</th>
      </tr>
    </thead>
    <tbody>
      {products.map((e) => {
        if (cartItems[e.id] > 0) {
          return (
            <tr key={e.id} className='cart-table-row'>
              <td>
                <img src={e.ProductImage[0].url} alt={e.ProductName} className='carticon-product-icon' />
              </td>
              <td>{e.ProductName}</td>
              <td>{e.Size}</td>
              <td>${e.ProductPrice.toFixed(2)}</td>
              <td>
                <button className='cartitems-quantity'>{cartItems[e.id]}</button>
              </td>
              <td>${(e.ProductPrice * cartItems[e.id]).toFixed(2)}</td>
              <td>
                <img
                  className='cartitems-remove-icon'
                  src={cross}
                  alt='Remove'
                  onClick={() => removeFromCart(e.id)}
                />
              </td>
            </tr>
          );
        }
        return null;
      })}
    </tbody>
  </table>
    <div className="cartitems-down">
      <div className="cartitems-total">
              <h2>cart Totals</h2>
              <div>
                  <div className="cartitems-total-item">
                            <p>Subtatal</p>
                            <p>${getTotalCartAmount()}</p>
                  </div> 
                  <hr/>
                  <div className="cartitems-total-item">
                            <p>Shipping Fee</p>
                            <p> Free</p>
                  </div>  
                  <hr/>
                  <div className="cartitems-total-item">
                            <h3>Total</h3>
                            <h3>${getTotalCartAmount()}</h3>
                  </div>    
              </div>
              <button>Proceed to checkout</button>
      </div>

    </div>
    </div>
  )
}

export default CartItems;
