import React, { useContext, useEffect } from 'react'
import { useParams } from 'react-router-dom';
import { ShopContext } from '../Context/ShopContext';
import Breadcrums from '../Components/Breadcrums/Breadcrums';
import Productdisplay from '../Components/Productdisplay/Productdisplay';
import DescriptionBox from '../Components/DescriptionBox/DescriptionBox';
import RelatedProducts from '../Components/RelatedProducts/RelatedProducts';
const Product = () => {

  const{productId} =useParams();


  console.log("hahaha",productId);


  const { products, loading } = useContext(ShopContext);

  // Log products whenever they are updated
  useEffect(() => {
    console.log("Updated Products:", products);
  }, [products]);

  // While products are still loading, show a loading state
  if (loading) {
    return <div>Loading products...</div>;
  }

  const product = products.find((e)=>e.id===productId);
  if (!product) {
    return <div>Product not found</div>; // Handle case when the product doesn't exist
  }


  return (
    <div>
      <Breadcrums theproduct ={product} />
      < Productdisplay product ={product}/>
      <DescriptionBox />
      <RelatedProducts />
    </div>
  )
}

export default Product
