import React, { useContext,useState,useEffect } from 'react'
import './CartItem.css'
import { ShopContext } from '../../Context/ShopContext'
import cross from '../Assets/cross.png'
import  AuthContext  from '../../Context/LoginAuthProvider'
const CartItems = () => {
       const {getTotalCartAmount, products,cartItems,removeFromCart} =useContext(ShopContext);
       const { auth } = useContext(AuthContext); 
       const [errorMessage, setErrorMessage] = useState('');
       console.log("this is cartitems:",cartItems);

  //address
      const [address, setAddress] = useState({
        address1:'',
        address2: '',
        city: '',
        state: '',
        country:'',
        postalCode: '',
    });
    const [showAddressForm, setShowAddressForm] = useState(false);


    const handleAddressChange = (e) => {
      setAddress({ ...address, [e.target.name]: e.target.value });
    };

    const validateAddress = () => {
      const { address1, city, state, country, postalCode } = address;
      return address1 && city && state && country && postalCode;
    };

    const handleAddressSubmit = (e) => {
      e.preventDefault();
      if (!validateAddress()) {
        setErrorMessage('Please fill in all required address fields.');
        return;
      }
      setShowAddressForm(false); // Close the form
      setErrorMessage('');
      alert('Address saved successfully!');
    };



       //const token = localStorage.getItem('authToken');


       //submit order
       const SubmitOrder = async (e) => {
        e.preventDefault();

        //const token = localStorage.getItem("authToken"); // Get token from localStorage for authentication
        if (!auth.id) {
          alert("You must be logged in to submit an order.");
          return;
        }

        //check if the auth.address is false 
        if(!auth.shippingAddress &&!validateAddress()){
          setShowAddressForm(true);
          setErrorMessage('Please provide a valid address.');
          return;
        }

        // Prepare order data (including product details from cart)
        const orderItems = products
          .filter((product) => cartItems[product.id] > 0) // Only include products that are in the cart
          .map((product) => ({
            productId: product.id,
            name: product.ProductName,
            price: product.ProductPrice,
            quantity: cartItems[product.id],
            total: (product.ProductPrice * cartItems[product.id]).toFixed(2),
          }));

        const customerId = auth.id; // Get user ID from AuthContext

        const orderData = {
          customerId,
          orderItems,
          totalAmount: getTotalCartAmount(),
          shippingAddress: address,
         // status: 'Pending', // Default order status
        };
        console.log("the data will be submited",orderData);

        try {
          const response = await fetch('http://localhost:5000/api/orders', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify(orderData),
          });

          const result = await response.json();

          if (response.ok) {
            // Handle success (e.g., clear cart, notify user)
            alert("Your order has been placed successfully!");
            // Optionally: clear the cart (if desired)
            // clearCart();
          } else {
            // Handle error from server
            setErrorMessage(result.message || "An error occurred while placing the order.");
          }
        } catch (error) {
          console.error("Error submitting order:", error);
          setErrorMessage("An error occurred while placing the order. Please try again later.");
        }
      };

  return (
    <div className='cartitems'>
      <table className='cart-table'>
    <thead>
      <tr className='cart-table-header'>
        <th>Product</th>
        <th>Title</th>
        <th>Size</th>
        <th>Price</th>
        <th>Quantity</th>
        <th>Total</th>
        <th>Remove</th>
      </tr>
    </thead>
    <tbody>
      {products.map((e) => {
        if (cartItems[e.id] > 0) {
          return (
            <tr key={e.id} className='cart-table-row'>
              <td>
                <img src={e.ProductImage[0].url} alt={e.ProductName} className='carticon-product-icon' />
              </td>
              <td>{e.ProductName}</td>
              <td>{e.Size}</td>
              <td>${e.ProductPrice.toFixed(2)}</td>
              <td>
                <button className='cartitems-quantity'>{cartItems[e.id]}</button>
              </td>
              <td>${(e.ProductPrice * cartItems[e.id]).toFixed(2)}</td>
              <td>
                <img
                  className='cartitems-remove-icon'
                  src={cross}
                  alt='Remove'
                  onClick={() => removeFromCart(e.id)}
                />
              </td>
            </tr>
          );
        }
        return null;
      })}
    </tbody>
  </table>
    <div className="cartitems-down">
      <div className="cartitems-total">
              <h2>cart Totals</h2>
              <div>
                  <div className="cartitems-total-item">
                            <p>Subtatal</p>
                            <p>${getTotalCartAmount()}</p>
                  </div> 
                  <hr/>
                  <div className="cartitems-total-item">
                            <p>Shipping Fee</p>
                            <p> Free</p>
                  </div>  
                  <hr/>
                  <div className="cartitems-total-item">
                            <h3>Total</h3>
                            <h3>${getTotalCartAmount()}</h3>
                  </div>    
              </div>
              <button onClick={SubmitOrder}>Proceed to checkout</button>
      </div>

    </div>
    {showAddressForm && (
        <form className="address-form" onSubmit={handleAddressSubmit}>
          <h3>Enter Your Address</h3>
          <label>
            Address Line 1:
            <input type="text" name="address1" value={address.address1} onChange={handleAddressChange} required />
          </label>
          <label>
            Address Line 2:
            <input type="text" name="address2" value={address.address2} onChange={handleAddressChange} />
          </label>
          <label>
            City:
            <input type="text" name="city" value={address.city} onChange={handleAddressChange} required />
          </label>
          <label>
            State:
            <input type="text" name="state" value={address.state} onChange={handleAddressChange} required />
          </label>
          <label>
            Country:
            <input type="text" name="country" value={address.country} onChange={handleAddressChange} required />
          </label>
          <label>
            Postal Code:
            <input type="text" name="postalCode" value={address.postalCode} onChange={handleAddressChange} required />
          </label>
          <button type="submit">Save Address</button>
        </form>
      )}

      {errorMessage && <p className="error-message">{errorMessage}</p>}


    </div>
  )
}

export default CartItems;
